process.stdout.setEncoding('utf8');
const puppeteer = require('puppeteer-extra');
const StealthPlugin = require('puppeteer-extra-plugin-stealth');
puppeteer.use(StealthPlugin());

(async () => {
  const targetUrl = process.argv[2];
  const titleSelector = process.argv[3];
  const priceSelector = process.argv[4];

  if (!targetUrl || !titleSelector || !priceSelector) {
    console.error('Usage: node combined.js <URL> <titleSelector> <priceSelector>');
    process.exit(1);
  }

  const browser = await puppeteer.launch({ headless: true });
  const page = await browser.newPage();

  await page.setExtraHTTPHeaders({
    'Accept-Language': 'en-US,en;q=0.9'
  });

  await page.setUserAgent(
    'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/114.0.0.0 Safari/537.36'
  );

  await page.goto(targetUrl, { waitUntil: 'networkidle2' });
  await page.waitForSelector(titleSelector);
  await page.waitForSelector(priceSelector);

  const results = await page.evaluate((titleSel, priceSel) => {
    const titles = Array.from(document.querySelectorAll(titleSel));
    const prices = Array.from(document.querySelectorAll(priceSel));
    const count = Math.min(titles.length, prices.length);

    return Array.from({ length: count }).map((_, i) => {
      const title = titles[i]?.textContent.trim() || 'N/A';

      let price = 'N/A';
      const el = prices[i];
      const span = el?.querySelector('span');

      if (span && span.nextSibling && span.nextSibling.nodeType === Node.TEXT_NODE) {
        const match = span.nextSibling.textContent.match(/[\d,.]+/);
        price = match ? match[0].replace(',', '.') : 'N/A';
      } else if (el) {
        const match = el.textContent.match(/[\d,.]+/);
        price = match ? match[0].replace(',', '.') : 'N/A';
      }

      return { title, price };
    });
  }, titleSelector, priceSelector);

  // Output results
  for (const { title, price } of results) {
  const cleanTitle = title
    .replace(/[‘’]/g, "'") 
    .replace(/\u00A0/g, ' ')
    .trim();

  console.log(`${cleanTitle} - ${price}`);
}

  await browser.close();
})();
