const puppeteer = require('puppeteer-extra');
const StealthPlugin = require('puppeteer-extra-plugin-stealth');
puppeteer.use(StealthPlugin());

(async () => {
  const targetUrl = process.argv[2];
  const priceSelector = process.argv[3];

  if (!targetUrl || !priceSelector) {
    console.error('Usage: node script.js <URL> <CSS selector>');
    process.exit(1);
  }

  const browser = await puppeteer.launch({ headless: true });
  const page = await browser.newPage();

  await page.setExtraHTTPHeaders({
    'Accept-Language': 'en-US,en;q=0.9'
  });

  await page.setUserAgent(
    'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/114.0.0.0 Safari/537.36'
  );

  await page.goto(targetUrl, { waitUntil: 'domcontentloaded' });
  
  await page.goto(targetUrl, { waitUntil: 'networkidle2' });
  
  await page.waitForSelector(priceSelector);

const prices = await page.evaluate((price) => {
	  return Array.from(document.querySelectorAll(price)).map(el => el.textContent.match(/[\d,.]+/));
	  return match ? match[0].replace(/,/g, '') : null;
}, priceSelector)

  for (const price of prices) {
    console.log(price);
  }
  
  await browser.close();
})();


